#include<bits/stdc++.h>
using namespace std;
using ll = long long;
int n, k;

string pos[4], neg[4]; vector<string> str[4]; 
int val[4] = {1, 108, 48, 1536};

void call(int n) {
  ll x = 0; string s = str[k][n % val[k]];
  for (int i = 0; i < s.size(); ++i) {
    ll v = i + 1; for (int j = 0; j < k; ++j) v *= (i+1);
    if (s[i] == '+') x += v;
    else x -= v;
  }
  int c = (n - x) / val[k];
  if (c > 0) for (int i = 0; i < c; ++i) s += pos[k];
  else for (int i = 0; i < -c; ++i) s += neg[k];
  cout << s;
}

const int N = 20000 + 5;
int sub[N];
void task1(int n) {
  if (n == 0) return cout << "++-", void();
  for (ll m = (sqrt(n)-1) * 2;; m++) {
    int s = m*(m+1)/2 - n;
    if (s & 1) continue;
    s /= 2; 
    for (int i = m; i; --i) 
      if (s >= i) sub[i] = 1, s -= i;
      else sub[i] = 0;
    if (s == 0) {
      string res;
      for (int i = 1; i <= m; ++i)
        if (sub[i]) res += '-'; else res += '+';
      cout << res; return;
    }
  }
}

